                    <div class="swiper">
                        <div class="swiper-wrapper">
                            <?php
                            $args = array(
                                'post_type'     => 'product',
                                'posts_per_page' => 9,
                                'tax_query' => array(
                                    array(
                                        'taxonomy' => 'product_cat',
                                        'field'    => 'id',
                                        'terms'    => 22,
                                    ),
                                ),
                                'orderby'       => 'date',
                                'order'         => 'desc'
                            );
                            $wp_template = new WP_Query($args);
                            if ($wp_template->have_posts()):
                                while ($wp_template->have_posts()):
                                    $wp_template->the_post();
                                    global $product;
                            ?>
                                    <div class="swiper-slide">
                                        <div class="category_slider_item">
                                            <div class="item_img_box">
                                                <a href="<?php the_permalink();?>">
                                                    <?php the_post_thumbnail('full', array('class' => 'img-fluid')); ?>
                                                </a>
                                            </div>
                                            <div class="item_info_box">
                                                <p class="item_info_box--cat">
                                                    <?php
                                                    $terms = wp_get_post_terms(get_the_ID(), 'product_cat');
                                                    foreach ($terms as $term) {
                                                        echo $term->name;
                                                    }
                                                    ?>
                                                </p>
                                                <h3 class="item_info_box--title">
                                                    <a href="<?php the_permalink(); ?>">
                                                        <?php the_title(); ?>
                                                    </a>
                                                </h3>
                                            </div>
                                            <div class="item_details_box">
                                                <div class="item_details_box--right d-flex align-items-center justify-content-start">
                                                    <i class="far fa-fire"></i>
                                                    <div class="item_details_price">
                                                        <?php
                                                        $regular_price = $product->get_regular_price();
                                                        $sale_price = $product->get_sale_price();
                                                        if ($product->is_on_sale()) {
                                                            if ($sale_price == '0') {
                                                                echo 'دانلود رایگان';
                                                            } else {
                                                                echo number_format($sale_price);
                                                                echo '<span class="currency">تومان</span>';
                                                            }
                                                        } else {
                                                            if ($regular_price == '0') {
                                                                echo 'دانلود رایگان';
                                                            } else {
                                                                echo number_format($regular_price);
                                                                echo '<span class="currency">تومان</span>';
                                                            }
                                                        }
                                                        ?>
                                                    </div>
                                                </div>
                                                <div class="item_details_box--left d-flex align-items-center justify-content-end">
                                                    <div class="item_details_rate">
                                                        <?php
                                                        $rate = $product->get_average_rating();
                                                        $rate_percent = ($rate * 100) / 5;
                                                        if ($rate == '0') {
                                                            echo 'محصول<br>وب دنج';
                                                        } else {
                                                            echo $rate_percent . '%';
                                                            echo '<span>رضایت</span>';
                                                        }
                                                        ?>
                                                    </div>
                                                    <div class="item_details_count">
                                                        <?php
                                                        $sale_count = $product->get_total_sales();
                                                        echo $sale_count;
                                                        ?>
                                                        <span>خرید</span>
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                            <?php

                                endwhile;
